    function __NFTXEligibility_init(
        bytes32 _merkleRoot,
        string memory _merkleReference,
        string memory _merkleLeavesURI,
        uint _minExpirationTime
    ) public initializer {
        super.__NFTXEligibility_init(
            _merkleRoot,
            _merkleReference,
            _merkleLeavesURI
