// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity =0.8.15;

// inheriting
import {PausableUpgradeable} from "@src/custom/PausableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";
import {ERC721PermitUpgradeable, ERC721EnumerableUpgradeable} from "@src/custom/tokens/ERC721/ERC721PermitUpgradeable.sol";
import {ERC1155HolderUpgradeable, ERC1155ReceiverUpgradeable, IERC165Upgradeable} from "@openzeppelin-upgradeable/contracts/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";

// libs
import {Base64} from "base64-sol/base64.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {SafeCast} from "@uni-core/libraries/SafeCast.sol";
import {FullMath} from "@uni-core/libraries/FullMath.sol";
import {TransferLib} from "@src/lib/TransferLib.sol";
import {FixedPoint128} from "@uni-core/libraries/FixedPoint128.sol";

// interfaces
import {IWETH9} from "@uni-periphery/interfaces/external/IWETH9.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {INFTXVaultV3} from "@src/interfaces/INFTXVaultV3.sol";
import {INFTXVaultFactoryV3} from "@src/interfaces/INFTXVaultFactoryV3.sol";
import {ITimelockExcludeList} from "@src/interfaces/ITimelockExcludeList.sol";
import {IERC20Metadata, IERC20} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IPermitAllowanceTransfer} from "@src/interfaces/external/IPermitAllowanceTransfer.sol";
import {InventoryStakingDescriptor} from "@src/custom/InventoryStakingDescriptor.sol";

import {INFTXInventoryStakingV3} from "@src/interfaces/INFTXInventoryStakingV3.sol";

/**
 * @title NFTX Inventory Staking V3
 * @author @apoorvlathey
 *
 * @dev lockId's:
 * 0: deposit & depositWithPermit2
 * 1: depositWithNFT
 * 2: withdraw
 * 3: collectWethFees
 * 4: increasePosition
 *
 * @notice Allows users to stake vTokens to earn fees in vTokens and WETH. The position is minted as xNFT.
 * @dev This contract must be on the feeExclusion list to avoid redeem fees, else revert.
 */

contract NFTXInventoryStakingV3Upgradeable is
    INFTXInventoryStakingV3,
    ERC721PermitUpgradeable,
    ERC1155HolderUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable
{
    using Strings for uint256;
    // =============================================================
    //                           CONSTANTS
    // =============================================================

    uint256 public constant override MINIMUM_LIQUIDITY = 1_000;
    uint256 private constant VTOKEN_TIMELOCK = 1 hours;

    IWETH9 public immutable override WETH;
    IPermitAllowanceTransfer public immutable override PERMIT2;
    INFTXVaultFactoryV3 public immutable override nftxVaultFactory;

    // "constants": only set during initialization
    ITimelockExcludeList public override timelockExcludeList;

    uint256 constant MAX_TIMELOCK = 14 days;
    uint256 constant MAX_EARLY_WITHDRAW_PENALTY = 1 ether;
    uint256 constant BASE = 1 ether;

    // =============================================================
    //                          VARIABLES
    // =============================================================

    /// @dev The ID of the next token that will be minted. Skips 0
    uint256 private _nextId;

    /// @dev timelock in seconds
    uint256 public override timelock;
    /// @dev the max penalty applicable. The penalty goes down linearly as the `timelockedUntil` approaches
    uint256 public override earlyWithdrawPenaltyInWei;

    /// @dev The token ID position data
    mapping(uint256 => Position) public override positions;

    /// @dev vaultId => VaultGlobal
    mapping(uint256 => VaultGlobal) public override vaultGlobal;

    InventoryStakingDescriptor public override descriptor;

    // =============================================================
    //                           INIT
    // =============================================================

    constructor(
        IWETH9 WETH_,
        IPermitAllowanceTransfer PERMIT2_,
        INFTXVaultFactoryV3 nftxVaultFactory_
    ) {
        WETH = WETH_;
        PERMIT2 = PERMIT2_;
        nftxVaultFactory = nftxVaultFactory_;
    }

    function __NFTXInventoryStaking_init(
        uint256 timelock_,
        uint256 earlyWithdrawPenaltyInWei_,
        ITimelockExcludeList timelockExcludeList_,
        InventoryStakingDescriptor descriptor_
    ) external override initializer {
        __ERC721PermitUpgradeable_init("NFTX Inventory Staking", "xNFT", "1");
        __Pausable_init();
        __ReentrancyGuard_init();

        if (timelock_ > MAX_TIMELOCK) revert TimelockTooLong();
        if (earlyWithdrawPenaltyInWei_ > MAX_EARLY_WITHDRAW_PENALTY)
            revert InvalidEarlyWithdrawPenalty();
        timelock = timelock_;
        earlyWithdrawPenaltyInWei = earlyWithdrawPenaltyInWei_;
        timelockExcludeList = timelockExcludeList_;
        descriptor = descriptor_;

        _nextId = 1;
    }

    // =============================================================
    //                     PUBLIC / EXTERNAL WRITE
    // =============================================================

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function deposit(
        uint256 vaultId,
        uint256 amount,
        address recipient,
        bytes calldata encodedPermit2,
        bool viaPermit2,
        bool forceTimelock
    ) external override returns (uint256 positionId) {
        address vToken = nftxVaultFactory.vault(vaultId);
        VaultGlobal storage _vaultGlobal = vaultGlobal[vaultId];

        uint256 preVTokenBalance = IERC20(vToken).balanceOf(address(this));

        if (viaPermit2) {
            if (encodedPermit2.length > 0) {
                (
                    address _owner,
                    IPermitAllowanceTransfer.PermitSingle memory permitSingle,
                    bytes memory signature
                ) = abi.decode(
                        encodedPermit2,
                        (address, IPermitAllowanceTransfer.PermitSingle, bytes)
                    );

                PERMIT2.permit(_owner, permitSingle, signature);
            }

            PERMIT2.transferFrom(
                msg.sender,
                address(this),
                SafeCast.toUint160(amount),
                address(vToken)
            );
        } else {
            IERC20(vToken).transferFrom(msg.sender, address(this), amount);
        }

        return
            _deposit(
                vaultId,
                amount,
                recipient,
                _vaultGlobal,
                preVTokenBalance,
                forceTimelock
            );
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function depositWithNFT(
        uint256 vaultId,
        uint256[] calldata tokenIds,
        uint256[] calldata amounts,
        address recipient
    ) external returns (uint256 positionId) {
        onlyOwnerIfPaused(1);

        address vToken = nftxVaultFactory.vault(vaultId);
        uint256 preVTokenBalance = IERC20(vToken).balanceOf(address(this));

        uint256 amount;
        {
            address assetAddress = INFTXVaultV3(vToken).assetAddress();

            if (!INFTXVaultV3(vToken).is1155()) {
                // tranfer NFTs from user to the vault
                TransferLib.transferFromERC721(
                    assetAddress,
                    address(vToken),
                    tokenIds
                );
            } else {
                IERC1155(assetAddress).safeBatchTransferFrom(
                    msg.sender,
                    address(this),
                    tokenIds,
                    amounts,
                    ""
                );

                IERC1155(assetAddress).setApprovalForAll(address(vToken), true);
            }

            // mint vTokens
            amount = INFTXVaultV3(vToken).mint(
                tokenIds,
                amounts,
                msg.sender,
                address(this)
            );
        }

        _mint(recipient, (positionId = _nextId++));

        {
            VaultGlobal storage _vaultGlobal = vaultGlobal[vaultId];

            uint256 vTokenShares = _mintVTokenShares(
                _vaultGlobal,
                amount,
                preVTokenBalance
            );

            uint256 _timelock = timelock;
            positions[positionId] = Position({
                nonce: 0,
                vaultId: vaultId,
                timelockedUntil: _getTimelockedUntil(vaultId, _timelock),
                timelock: _timelock,
                vTokenTimelockedUntil: 0,
                vTokenShareBalance: vTokenShares,
                wethFeesPerVTokenShareSnapshotX128: _vaultGlobal
                    .globalWethFeesPerVTokenShareX128,
                wethOwed: 0
            });
        }

        emit DepositWithNFT(vaultId, positionId, tokenIds, amounts);
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function increasePosition(
        uint256 positionId,
        uint256 amount,
        bytes calldata encodedPermit2,
        bool viaPermit2,
        bool forceTimelock
    ) external {
        if (ownerOf(positionId) != msg.sender) revert NotPositionOwner();

        Position storage position = positions[positionId];

        uint256 vaultId = position.vaultId;
        address vToken = nftxVaultFactory.vault(vaultId);

        uint256 preVTokenBalance = IERC20(vToken).balanceOf(address(this));

        if (viaPermit2) {
            if (encodedPermit2.length > 0) {
                (
                    address _owner,
                    IPermitAllowanceTransfer.PermitSingle memory permitSingle,
                    bytes memory signature
                ) = abi.decode(
                        encodedPermit2,
                        (address, IPermitAllowanceTransfer.PermitSingle, bytes)
                    );

                PERMIT2.permit(_owner, permitSingle, signature);
            }

            PERMIT2.transferFrom(
                msg.sender,
                address(this),
                SafeCast.toUint160(amount),
                address(vToken)
            );
        } else {
            IERC20(vToken).transferFrom(msg.sender, address(this), amount);
        }

        return
            _increasePosition(
                positionId,
                position,
                vaultId,
                amount,
                preVTokenBalance,
                forceTimelock
            );
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function withdraw(
        uint256 positionId,
        uint256 vTokenShares,
        uint256[] calldata nftIds,
        uint256 vTokenPremiumLimit
    ) external payable override nonReentrant {
        onlyOwnerIfPaused(2);

        if (ownerOf(positionId) != msg.sender) revert NotPositionOwner();

        address vToken;
        uint256 vTokenOwed;
        uint256 wethOwed;
        uint256 _timelockedUntil;
        {
            Position storage position = positions[positionId];

            if (block.timestamp <= position.vTokenTimelockedUntil)
                revert Timelocked();
            uint256 positionVTokenShareBalance = position.vTokenShareBalance;
            if (positionVTokenShareBalance < vTokenShares)
                revert InsufficientVTokenShares();

            VaultGlobal storage _vaultGlobal;
            {
                uint256 vaultId = position.vaultId;
                _vaultGlobal = vaultGlobal[vaultId];
                vToken = nftxVaultFactory.vault(vaultId);
            }

            // cache
            uint256 _totalVTokenShares = _vaultGlobal.totalVTokenShares;
            // withdraw vTokens corresponding to the vTokenShares requested
            vTokenOwed =
                (IERC20(vToken).balanceOf(address(this)) * vTokenShares) /
                _totalVTokenShares;

            {
                // cache
                uint256 _globalWethFeesPerVTokenShareX128 = _vaultGlobal
                    .globalWethFeesPerVTokenShareX128;
                // withdraw all the weth fees accrued
                {
                    wethOwed =
                        _calcWethOwed(
                            _globalWethFeesPerVTokenShareX128,
                            position.wethFeesPerVTokenShareSnapshotX128,
                            positionVTokenShareBalance
                        ) +
                        position.wethOwed;
                }
                position
                    .wethFeesPerVTokenShareSnapshotX128 = _globalWethFeesPerVTokenShareX128;
                position.wethOwed = 0;
            }

            // cache
            _timelockedUntil = position.timelockedUntil;

            if (block.timestamp <= _timelockedUntil) {
                // Eg: timelock = 10 days, vTokenOwed = 100, penalty% = 5%
                // Case 1: Instant withdraw, with 10 days left
                // penaltyAmt = 100 * 5% = 5
                // Case 2: With 2 days timelock left
                // penaltyAmt = (100 * 5%) * 2 / 10 = 1
                uint256 vTokenPenalty = ((_timelockedUntil - block.timestamp) *
                    vTokenOwed *
                    earlyWithdrawPenaltyInWei) / (position.timelock * BASE);
                vTokenOwed -= vTokenPenalty;
            }

            // in case of penalty, more shares are burned than the corresponding vToken balance
            // resulting in an increase of `pricePerShareVToken`, hence the penalty collected is distributed amongst other stakers
            _vaultGlobal.totalVTokenShares = _totalVTokenShares - vTokenShares;
            position.vTokenShareBalance -= vTokenShares;
        }

        uint256 nftCount = nftIds.length;
        if (nftCount > 0) {
            // check if we have sufficient vTokens
            uint256 requiredVTokens = nftCount * BASE;
            if (vTokenOwed < requiredVTokens) revert InsufficientVTokens();

            {
                // send vToken residue first
                {
                    uint256 vTokenResidue;
                    unchecked {
                        vTokenResidue = vTokenOwed - requiredVTokens;
                    }
                    if (vTokenResidue > 0) {
                        IERC20(vToken).transfer(msg.sender, vTokenResidue);
                    }
                }

                INFTXVaultV3(vToken).redeem{value: msg.value}(
                    nftIds,
                    msg.sender,
                    0,
                    vTokenPremiumLimit,
                    _timelockedUntil == 0 // forcing fees for positions which never were under timelock (or else they can bypass redeem fees as deposit was made in vTokens)
                );
            }
        } else {
            // transfer tokens to the user
            IERC20(vToken).transfer(msg.sender, vTokenOwed);
        }
        WETH.transfer(msg.sender, wethOwed);

        uint256 ethResidue = address(this).balance;
        TransferLib.transferETH(msg.sender, ethResidue);

        emit Withdraw(positionId, vTokenShares, vTokenOwed, wethOwed);
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function combinePositions(
        uint256 parentPositionId,
        uint256[] calldata childPositionIds
    ) external override {
        // `ownerOf` handles invalid positionId
        if (ownerOf(parentPositionId) != msg.sender) revert NotPositionOwner();
        Position storage parentPosition = positions[parentPositionId];
        uint256 parentVaultId = parentPosition.vaultId;

        VaultGlobal storage _vaultGlobal = vaultGlobal[parentVaultId];

        if (
            block.timestamp <= parentPosition.timelockedUntil ||
            block.timestamp <= parentPosition.vTokenTimelockedUntil
        ) revert Timelocked();

        // cache
        uint256 _globalWethFeesPerVTokenShareX128 = _vaultGlobal
            .globalWethFeesPerVTokenShareX128;
        uint256 _parentVTokenShareBalance = parentPosition.vTokenShareBalance;

        // weth owed for the parent position
        uint256 netWethOwed = _calcWethOwed(
            _globalWethFeesPerVTokenShareX128,
            parentPosition.wethFeesPerVTokenShareSnapshotX128,
            _parentVTokenShareBalance
        );
        uint256 childrenPositionsCount = childPositionIds.length;
        for (uint256 i; i < childrenPositionsCount; ) {
            if (childPositionIds[i] == parentPositionId)
                revert ParentChildSame();
            // `ownerOf` handles invalid positionId
            if (ownerOf(childPositionIds[i]) != msg.sender)
                revert NotPositionOwner();

            Position storage childPosition = positions[childPositionIds[i]];
            if (
                block.timestamp <= childPosition.timelockedUntil ||
                block.timestamp <= childPosition.vTokenTimelockedUntil
            ) revert Timelocked();
            if (childPosition.vaultId != parentVaultId)
                revert VaultIdMismatch();

            // cache
            uint256 _childVTokenShareBalance = childPosition.vTokenShareBalance;

            // add weth owed for this child position
            netWethOwed +=
                _calcWethOwed(
                    _globalWethFeesPerVTokenShareX128,
                    childPosition.wethFeesPerVTokenShareSnapshotX128,
                    _childVTokenShareBalance
                ) +
                childPosition.wethOwed;
            // transfer vToken share balance to parent position
            _parentVTokenShareBalance += _childVTokenShareBalance;
            childPosition.vTokenShareBalance = 0;
            childPosition.wethOwed = 0;

            unchecked {
                ++i;
            }
        }

        // set new wethFeesPerVTokenShare snapshot
        parentPosition
            .wethFeesPerVTokenShareSnapshotX128 = _globalWethFeesPerVTokenShareX128;
        parentPosition.vTokenShareBalance = _parentVTokenShareBalance;

        // add net wethOwed to the parent position
        parentPosition.wethOwed += netWethOwed;

        emit CombinePositions(parentPositionId, childPositionIds);
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function collectWethFees(uint256[] calldata positionIds) external {
        onlyOwnerIfPaused(3);

        uint256 totalWethOwed;
        uint256 wethOwed;
        uint256 len = positionIds.length;
        for (uint256 i; i < len; ) {
            if (ownerOf(positionIds[i]) != msg.sender)
                revert NotPositionOwner();

            Position storage position = positions[positionIds[i]];
            VaultGlobal storage _vaultGlobal = vaultGlobal[position.vaultId];

            // cache
            uint256 _globalWethFeesPerVTokenShareX128 = _vaultGlobal
                .globalWethFeesPerVTokenShareX128;

            wethOwed =
                _calcWethOwed(
                    _globalWethFeesPerVTokenShareX128,
                    position.wethFeesPerVTokenShareSnapshotX128,
                    position.vTokenShareBalance
                ) +
                position.wethOwed;
            totalWethOwed += wethOwed;

            position
                .wethFeesPerVTokenShareSnapshotX128 = _globalWethFeesPerVTokenShareX128;
            position.wethOwed = 0;

            emit CollectWethFees(positionIds[i], wethOwed);

            unchecked {
                ++i;
            }
        }

        WETH.transfer(msg.sender, totalWethOwed);
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function receiveWethRewards(
        uint256 vaultId,
        uint256 wethAmount
    ) external override returns (bool rewardsDistributed) {
        if (msg.sender != nftxVaultFactory.feeDistributor())
            revert SenderNotFeeDistributor();

        VaultGlobal storage _vaultGlobal = vaultGlobal[vaultId];
        if (_vaultGlobal.totalVTokenShares == 0) {
            return false;
        }
        rewardsDistributed = true;

        WETH.transferFrom(msg.sender, address(this), wethAmount);
        _vaultGlobal.globalWethFeesPerVTokenShareX128 += FullMath.mulDiv(
            wethAmount,
            FixedPoint128.Q128,
            _vaultGlobal.totalVTokenShares
        );
    }

    // =============================================================
    //                        ONLY OWNER WRITE
    // =============================================================

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function setTimelock(uint256 timelock_) external override onlyOwner {
        if (timelock_ > MAX_TIMELOCK) revert TimelockTooLong();

        timelock = timelock_;
        emit UpdateTimelock(timelock_);
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function setEarlyWithdrawPenalty(
        uint256 earlyWithdrawPenaltyInWei_
    ) external override onlyOwner {
        if (earlyWithdrawPenaltyInWei_ > MAX_EARLY_WITHDRAW_PENALTY)
            revert InvalidEarlyWithdrawPenalty();

        earlyWithdrawPenaltyInWei = earlyWithdrawPenaltyInWei_;
        emit UpdateEarlyWithdrawPenalty(earlyWithdrawPenaltyInWei_);
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function setDescriptor(
        InventoryStakingDescriptor descriptor_
    ) external override onlyOwner {
        if (address(descriptor_) == address(0)) revert ZeroAddress();

        descriptor = descriptor_;
    }

    // =============================================================
    //                     PUBLIC / EXTERNAL VIEW
    // =============================================================

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function pricePerShareVToken(
        uint256 vaultId
    ) external view override returns (uint256) {
        VaultGlobal storage _vaultGlobal = vaultGlobal[vaultId];
        address vToken = nftxVaultFactory.vault(vaultId);

        return
            (IERC20(vToken).balanceOf(address(this)) * BASE) /
            _vaultGlobal.totalVTokenShares;
    }

    /**
     * @inheritdoc INFTXInventoryStakingV3
     */
    function wethBalance(
        uint256 positionId
    ) public view override returns (uint256) {
        Position memory position = positions[positionId];
        VaultGlobal memory _vaultGlobal = vaultGlobal[position.vaultId];

        return
            _calcWethOwed(
                _vaultGlobal.globalWethFeesPerVTokenShareX128,
                position.wethFeesPerVTokenShareSnapshotX128,
                position.vTokenShareBalance
            ) + position.wethOwed;
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(
            ERC1155ReceiverUpgradeable,
            ERC721EnumerableUpgradeable,
            IERC165Upgradeable
        )
        returns (bool)
    {
        return
            interfaceId == type(ERC721PermitUpgradeable).interfaceId ||
            interfaceId == type(ERC1155ReceiverUpgradeable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function tokenURI(
        uint256 tokenId
    ) public view override returns (string memory) {
        Position memory position = positions[tokenId];
        address vToken = nftxVaultFactory.vault(position.vaultId);

        uint256 vTokenBalance = (IERC20(vToken).balanceOf(address(this)) *
            position.vTokenShareBalance) /
            vaultGlobal[position.vaultId].totalVTokenShares;

        string memory vTokenSymbol = IERC20Metadata(vToken).symbol();

        return
            descriptor.tokenURI(
                tokenId,
                position.vaultId,
                vToken,
                vTokenSymbol,
                vTokenBalance,
                wethBalance(tokenId),
                position.timelockedUntil
            );
    }

    // =============================================================
    //                        INTERNAL HELPERS
    // =============================================================

    function _deposit(
        uint256 vaultId,
        uint256 amount,
        address recipient,
        VaultGlobal storage _vaultGlobal,
        uint256 preVTokenBalance,
        bool forceTimelock
    ) internal returns (uint256 positionId) {
        onlyOwnerIfPaused(0);

        _mint(recipient, (positionId = _nextId++));

        uint256 vTokenShares = _mintVTokenShares(
            _vaultGlobal,
            amount,
            preVTokenBalance
        );

        uint256 _timelock = timelock;
        positions[positionId] = Position({
            nonce: 0,
            vaultId: vaultId,
            timelockedUntil: forceTimelock ? block.timestamp + _timelock : 0,
            timelock: _timelock,
            vTokenTimelockedUntil: block.timestamp + VTOKEN_TIMELOCK,
            vTokenShareBalance: vTokenShares,
            wethFeesPerVTokenShareSnapshotX128: _vaultGlobal
                .globalWethFeesPerVTokenShareX128,
            wethOwed: 0
        });

        emit Deposit(vaultId, positionId, amount, forceTimelock);
    }

    function _increasePosition(
        uint256 positionId,
        Position storage position,
        uint256 vaultId,
        uint256 amount,
        uint256 preVTokenBalance,
        bool forceTimelock
    ) internal {
        onlyOwnerIfPaused(4);

        // only allow for positions created with just vTokens
        if (position.timelockedUntil > 0)
            revert PositionNotCreatedWithVTokens();

        VaultGlobal storage _vaultGlobal = vaultGlobal[vaultId];

        // cache
        uint256 _globalWethFeesPerVTokenShareX128 = _vaultGlobal
            .globalWethFeesPerVTokenShareX128;
        uint256 _preVTokenShareBalance = position.vTokenShareBalance;
        // account for weth fees accumulated till now
        position.wethOwed =
            _calcWethOwed(
                _globalWethFeesPerVTokenShareX128,
                position.wethFeesPerVTokenShareSnapshotX128,
                _preVTokenShareBalance
            ) +
            position.wethOwed;
        position
            .wethFeesPerVTokenShareSnapshotX128 = _globalWethFeesPerVTokenShareX128;

        position.vTokenShareBalance =
            _preVTokenShareBalance +
            _mintVTokenShares(_vaultGlobal, amount, preVTokenBalance);

        if (forceTimelock) {
            uint256 _timelock = timelock;
            position.timelockedUntil = block.timestamp + _timelock;
            position.timelock = _timelock;
        }
        position.vTokenTimelockedUntil = block.timestamp + VTOKEN_TIMELOCK;

        emit IncreasePosition(vaultId, positionId, amount);
    }

    function _mintVTokenShares(
        VaultGlobal storage _vaultGlobal,
        uint256 amount,
        uint256 preVTokenBalance
    ) internal returns (uint256 vTokenShares) {
        // cache
        uint256 _totalVTokenShares = _vaultGlobal.totalVTokenShares;
        if (_totalVTokenShares == 0) {
            if (amount < MINIMUM_LIQUIDITY) revert LiquidityBelowMinimum();
            unchecked {
                vTokenShares = amount - MINIMUM_LIQUIDITY;
            }
            // permanently locked to avoid front-running attack
            _totalVTokenShares = MINIMUM_LIQUIDITY;
        } else {
            vTokenShares = (amount * _totalVTokenShares) / preVTokenBalance;
        }
        if (vTokenShares == 0) revert ZeroVTokenShares();

        _vaultGlobal.totalVTokenShares = _totalVTokenShares + vTokenShares;
    }

    function _getTimelockedUntil(
        uint256 vaultId,
        uint256 _timelock
    ) internal view returns (uint256) {
        return
            timelockExcludeList.isExcluded(msg.sender, vaultId)
                ? 0
                : block.timestamp + _timelock;
    }

    function _calcWethOwed(
        uint256 globalWethFeesPerVTokenShareX128,
        uint256 positionWethFeesPerVTokenShareSnapshotX128,
        uint256 positionVTokenShareBalance
    ) internal pure returns (uint256 wethOwed) {
        wethOwed = FullMath.mulDiv(
            globalWethFeesPerVTokenShareX128 -
                positionWethFeesPerVTokenShareSnapshotX128,
            positionVTokenShareBalance,
            FixedPoint128.Q128
        );
    }

    function _getAndIncrementNonce(
        uint256 tokenId
    ) internal override returns (uint256) {
        return uint256(positions[tokenId].nonce++);
    }

    receive() external payable {}
}

src/NFTXRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.15;

// inheriting
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import {ERC1155Holder} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

// libs
import {SafeCast} from "@uni-core/libraries/SafeCast.sol";
import {TransferLib} from "@src/lib/TransferLib.sol";
import {PoolAddress} from "@uni-periphery/libraries/PoolAddress.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// interfaces
import {IWETH9} from "@uni-periphery/interfaces/external/IWETH9.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {IQuoterV2} from "@uni-periphery/interfaces/IQuoterV2.sol";
import {INFTXVaultV3} from "@src/interfaces/INFTXVaultV3.sol";
import {IUniswapV3Factory} from "@uni-core/interfaces/IUniswapV3Factory.sol";
import {INFTXVaultFactoryV3} from "@src/interfaces/INFTXVaultFactoryV3.sol";
import {INFTXFeeDistributorV3} from "@src/interfaces/INFTXFeeDistributorV3.sol";
import {ISwapRouter, SwapRouter} from "@uni-periphery/SwapRouter.sol";
import {INFTXInventoryStakingV3} from "@src/interfaces/INFTXInventoryStakingV3.sol";
import {IPermitAllowanceTransfer} from "@src/interfaces/external/IPermitAllowanceTransfer.sol";
import {INonfungiblePositionManager} from "@uni-periphery/interfaces/INonfungiblePositionManager.sol";

import {INFTXRouter} from "@src/interfaces/INFTXRouter.sol";

/**
 * @title NFTX Router
 * @author @apoorvlathey
 *
 * @notice Router to facilitate vault tokens minting/burning + addition/removal of concentrated liquidity
 * @dev This router must be excluded from the vault fees, as vault fees handled via custom logic here. Also should be excluded from timelocks on NonfungiblePositionManager.
 */
contract NFTXRouter is INFTXRouter, Ownable, ERC721Holder, ERC1155Holder {
    using SafeERC20 for IERC20;

    // =============================================================
    //                           CONSTANTS
    // =============================================================

    address public immutable override WETH;
    IPermitAllowanceTransfer public immutable override PERMIT2;

    INonfungiblePositionManager public immutable override positionManager;
    SwapRouter public immutable override router;
    IQuoterV2 public immutable override quoter;
    INFTXVaultFactoryV3 public immutable override nftxVaultFactory;
    INFTXInventoryStakingV3 public immutable override inventoryStaking;

    // =============================================================
    //                          VARIABLES
    // =============================================================

    uint256 public override lpTimelock;
    /// @notice the max penalty applicable. The penalty goes down linearly as the `timelockedUntil` approaches
    uint256 public override earlyWithdrawPenaltyInWei;
    /// @notice the vToken dust amount during add/increase liquidity, above which the vTokens get staked into inventory
    uint256 public override vTokenDustThreshold;

    constructor(
        INonfungiblePositionManager positionManager_,
        SwapRouter router_,
        IQuoterV2 quoter_,
        INFTXVaultFactoryV3 nftxVaultFactory_,
        IPermitAllowanceTransfer PERMIT2_,
        uint256 lpTimelock_,
        uint256 earlyWithdrawPenaltyInWei_,
        uint256 vTokenDustThreshold_,
        INFTXInventoryStakingV3 inventoryStaking_
    ) {
        positionManager = positionManager_;
        router = router_;
        quoter = quoter_;
        nftxVaultFactory = nftxVaultFactory_;
        PERMIT2 = PERMIT2_;

        if (lpTimelock_ == 0) revert ZeroLPTimelock();
        lpTimelock = lpTimelock_;

        if (earlyWithdrawPenaltyInWei_ > 1 ether)
            revert InvalidEarlyWithdrawPenalty();
        earlyWithdrawPenaltyInWei = earlyWithdrawPenaltyInWei_;

        vTokenDustThreshold = vTokenDustThreshold_;
        inventoryStaking = inventoryStaking_;

        WETH = positionManager_.WETH9();
    }

    // =============================================================
    //                     PUBLIC / EXTERNAL WRITE
    // =============================================================

    /**
     * @inheritdoc INFTXRouter
     */
    function addLiquidity(
        AddLiquidityParams calldata params
    ) external payable override returns (uint256 positionId) {
        INFTXVaultV3 vToken = INFTXVaultV3(
            nftxVaultFactory.vault(params.vaultId)
        );

        if (params.vTokensAmount > 0) {
            vToken.transferFrom(
                msg.sender,
                address(this),
                params.vTokensAmount
            );
        }

        return _addLiquidity(params, vToken);
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function addLiquidityWithPermit2(
        AddLiquidityParams calldata params,
        bytes calldata encodedPermit2
    ) external payable override returns (uint256 positionId) {
        INFTXVaultV3 vToken = INFTXVaultV3(
            nftxVaultFactory.vault(params.vaultId)
        );

        if (encodedPermit2.length > 0) {
            (
                address owner,
                IPermitAllowanceTransfer.PermitSingle memory permitSingle,
                bytes memory signature
            ) = abi.decode(
                    encodedPermit2,
                    (address, IPermitAllowanceTransfer.PermitSingle, bytes)
                );

            PERMIT2.permit(owner, permitSingle, signature);
        }

        if (params.vTokensAmount > 0) {
            PERMIT2.transferFrom(
                msg.sender,
                address(this),
                SafeCast.toUint160(params.vTokensAmount),
                address(vToken)
            );
        }

        return _addLiquidity(params, vToken);
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function increaseLiquidity(
        IncreaseLiquidityParams calldata params
    ) external payable override {
        INFTXVaultV3 vToken = INFTXVaultV3(
            nftxVaultFactory.vault(params.vaultId)
        );

        if (params.vTokensAmount > 0) {
            vToken.transferFrom(
                msg.sender,
                address(this),
                params.vTokensAmount
            );
        }

        return _increaseLiquidity(params, vToken);
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function increaseLiquidityWithPermit2(
        IncreaseLiquidityParams calldata params,
        bytes calldata encodedPermit2
    ) external payable override {
        INFTXVaultV3 vToken = INFTXVaultV3(
            nftxVaultFactory.vault(params.vaultId)
        );

        if (encodedPermit2.length > 0) {
            (
                address owner,
                IPermitAllowanceTransfer.PermitSingle memory permitSingle,
                bytes memory signature
            ) = abi.decode(
                    encodedPermit2,
                    (address, IPermitAllowanceTransfer.PermitSingle, bytes)
                );

            PERMIT2.permit(owner, permitSingle, signature);
        }

        if (params.vTokensAmount > 0) {
            PERMIT2.transferFrom(
                msg.sender,
                address(this),
                SafeCast.toUint160(params.vTokensAmount),
                address(vToken)
            );
        }

        return _increaseLiquidity(params, vToken);
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function removeLiquidity(
        RemoveLiquidityParams calldata params
    ) external payable override {
        if (positionManager.ownerOf(params.positionId) != msg.sender)
            revert NotPositionOwner();

        // decrease liquidity of the position (this contract is excluded from the timelocks)
        positionManager.decreaseLiquidity(
            INonfungiblePositionManager.DecreaseLiquidityParams({
                tokenId: params.positionId,
                liquidity: params.liquidity,
                amount0Min: params.amount0Min,
                amount1Min: params.amount1Min,
                deadline: params.deadline
            })
        );

        INFTXVaultV3 vToken;
        uint256 vTokenAmt;
        uint256 wethAmt;
        {
            // collect vtokens & weth from removing liquidity + earned fees
            (uint256 amount0, uint256 amount1) = positionManager.collect(
                INonfungiblePositionManager.CollectParams({
                    tokenId: params.positionId,
                    recipient: address(this),
                    amount0Max: type(uint128).max,
                    amount1Max: type(uint128).max
                })
            );

            vToken = INFTXVaultV3(nftxVaultFactory.vault(params.vaultId));

            bool _isVToken0 = isVToken0(address(vToken));
            (vTokenAmt, wethAmt) = _isVToken0
                ? (amount0, amount1)
                : (amount1, amount0);
        }

        // checking timelock penalty
        uint256 _timelockedUntil = positionManager.lockedUntil(
            params.positionId
        );
        uint256 _timelock = positionManager.timelock(params.positionId);
        if (block.timestamp <= _timelockedUntil) {
            if (vTokenAmt > 0) {
                // Eg: lpTimelock = 10 days, vTokenAmt = 100, penalty% = 5%
                // Case 1: Instant withdraw, with 10 days left
                // penaltyAmt = 100 * 5% = 5
                // Case 2: With 2 days timelock left
                // penaltyAmt = (100 * 5%) * 2 / 10 = 1
                uint256 vTokenPenalty = ((_timelockedUntil - block.timestamp) *
                    vTokenAmt *
                    earlyWithdrawPenaltyInWei) / (_timelock * 1 ether);

                // distribute this penalty
                {
                    (, , , , uint24 fee, , , , , , , ) = positionManager
                        .positions(params.positionId);
                    address pool = IUniswapV3Factory(router.factory()).getPool(
                        address(vToken),
                        WETH,
                        fee
                    );

                    address feeDistributor = nftxVaultFactory.feeDistributor();
                    vToken.transfer(feeDistributor, vTokenPenalty);
                    INFTXFeeDistributorV3(feeDistributor)
                        .distributeVTokensToPool(
                            pool,
                            address(vToken),
                            vTokenPenalty
                        );
                }

                vTokenAmt -= vTokenPenalty;
            }
        }

        // No NFTs to redeem, directly withdraw vTokens
        if (params.nftIds.length == 0 && vTokenAmt > 0) {
            if (msg.value > 0) revert NoETHFundsNeeded();
            vToken.transfer(msg.sender, vTokenAmt);
        } else {
            // if withdrawn WETH is insufficient to pay for vault fees, user sends ETH (can be excess, as refunded back) along with the transaction
            if (msg.value > 0) {
                IWETH9(WETH).deposit{value: msg.value}();
                wethAmt += msg.value;
            }

            // if the position has completed its timelock, then we shouldn't charge redeem fees.
            bool chargeFees = positionManager.lockedUntil(params.positionId) ==
                0;

            // burn vTokens to provided tokenIds array. Forcing to deduct vault fees
            if (chargeFees) {
                TransferLib.unSafeMaxApprove(WETH, address(vToken), wethAmt);
            }

            uint256 vTokenBurned = params.nftIds.length * 1 ether;
            if (vTokenAmt < vTokenBurned) revert InsufficientVTokens();

            uint256 wethFees = vToken.redeem(
                params.nftIds,
                msg.sender,
                wethAmt,
                params.vTokenPremiumLimit,
                chargeFees
            );
            wethAmt -= wethFees;

            // if more vTokens collected than burned
            uint256 vTokenResidue = vTokenAmt - vTokenBurned;

            if (vTokenResidue > 0) {
                vToken.transfer(msg.sender, vTokenResidue);
            }
        }
        // convert remaining WETH to ETH & send to user
        IWETH9(WETH).withdraw(wethAmt);
        TransferLib.transferETH(msg.sender, wethAmt);

        emit RemoveLiquidity(
            params.positionId,
            params.vaultId,
            vTokenAmt,
            wethAmt
        );
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function sellNFTs(
        SellNFTsParams calldata params
    ) external payable override returns (uint256 wethReceived) {
        INFTXVaultV3 vToken = INFTXVaultV3(
            nftxVaultFactory.vault(params.vaultId)
        );
        address assetAddress = INFTXVaultV3(address(vToken)).assetAddress();

        if (!vToken.is1155()) {
            // tranfer NFTs from user to the vault
            TransferLib.transferFromERC721(
                assetAddress,
                address(vToken),
                params.nftIds
            );
        } else {
            IERC1155(assetAddress).safeBatchTransferFrom(
                msg.sender,
                address(this),
                params.nftIds,
                params.nftAmounts,
                ""
            );

            IERC1155(assetAddress).setApprovalForAll(address(vToken), true);
        }

        // mint vToken
        uint256 vTokensAmount = vToken.mint(
            params.nftIds,
            params.nftAmounts,
            msg.sender,
            address(this)
        );

        TransferLib.unSafeMaxApprove(
            address(vToken),
            address(router),
            vTokensAmount
        );

        wethReceived = router.exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: address(vToken),
                tokenOut: WETH,
                fee: params.fee,
                recipient: address(this),
                deadline: params.deadline,
                amountIn: vTokensAmount,
                amountOutMinimum: params.amountOutMinimum,
                sqrtPriceLimitX96: params.sqrtPriceLimitX96
            })
        );

        // if received WETH is insufficient to pay for vault fees, user sends ETH (can be excess, as refunded back) along with the transaction
        if (msg.value > 0) {
            IWETH9(WETH).deposit{value: msg.value}();
            wethReceived += msg.value;
        }
        // distributing vault fees with the wethReceived
        uint256 nftCount = !vToken.is1155()
            ? params.nftIds.length
            : _sum1155Ids(params.nftIds, params.nftAmounts);

        uint256 wethFees = _ethMintFees(vToken, nftCount);
        _distributeVaultFees(params.vaultId, wethFees, true);
        uint256 wethRemaining = wethReceived - wethFees; // if underflow, then revert desired

        // convert remaining WETH to ETH & send to user
        IWETH9(WETH).withdraw(wethRemaining);
        TransferLib.transferETH(msg.sender, wethRemaining);

        emit SellNFTs(nftCount, wethRemaining);
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function buyNFTs(BuyNFTsParams calldata params) external payable override {
        INFTXVaultV3 vToken = INFTXVaultV3(
            nftxVaultFactory.vault(params.vaultId)
        );
        uint256 vTokenAmt = params.nftIds.length * 1 ether;

        IWETH9(WETH).deposit{value: msg.value}();
        TransferLib.unSafeMaxApprove(WETH, address(router), msg.value);
        uint256 wethSpent = router.exactOutputSingle(
            ISwapRouter.ExactOutputSingleParams({
                tokenIn: WETH,
                tokenOut: address(vToken),
                fee: params.fee,
                recipient: address(this),
                deadline: params.deadline,
                amountOut: vTokenAmt,
                amountInMaximum: msg.value,
                sqrtPriceLimitX96: params.sqrtPriceLimitX96
            })
        );

        // unwrap vTokens to tokenIds specified, and send to sender. Forcing to deduct vault fees
        uint256 wethLeft = msg.value - wethSpent;
        TransferLib.unSafeMaxApprove(WETH, address(vToken), wethLeft);
        uint256 wethFees = vToken.redeem(
            params.nftIds,
            msg.sender,
            wethLeft,
            params.vTokenPremiumLimit,
            true
        );

        wethLeft -= wethFees;
        // refund extra ETH
        if (wethLeft > 0) {
            IWETH9(WETH).withdraw(wethLeft);

            TransferLib.transferETH(msg.sender, wethLeft);
        }

        emit BuyNFTs(params.nftIds.length, wethSpent + wethFees);
    }

    // =============================================================
    //                        ONLY OWNER WRITE
    // =============================================================

    /**
     * @inheritdoc INFTXRouter
     */
    function rescueTokens(IERC20 token) external override onlyOwner {
        if (address(token) != address(0)) {
            uint256 balance = token.balanceOf(address(this));
            token.safeTransfer(msg.sender, balance);
        } else {
            uint256 balance = address(this).balance;
            TransferLib.transferETH(msg.sender, balance);
        }
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function setLpTimelock(uint256 lpTimelock_) external override onlyOwner {
        if (lpTimelock_ == 0) revert ZeroLPTimelock();

        lpTimelock = lpTimelock_;
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function setVTokenDustThreshold(
        uint256 vTokenDustThreshold_
    ) external override onlyOwner {
        vTokenDustThreshold = vTokenDustThreshold_;
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function setEarlyWithdrawPenalty(
        uint256 earlyWithdrawPenaltyInWei_
    ) external onlyOwner {
        if (earlyWithdrawPenaltyInWei_ > 1 ether)
            revert InvalidEarlyWithdrawPenalty();

        earlyWithdrawPenaltyInWei = earlyWithdrawPenaltyInWei_;
    }

    // =============================================================
    //                     PUBLIC / EXTERNAL VIEW
    // =============================================================

    /**
     * @inheritdoc INFTXRouter
     */
    function quoteBuyNFTs(
        address vtoken,
        uint256 nftsCount,
        uint24 fee,
        uint160 sqrtPriceLimitX96
    ) external override returns (uint256 ethRequired) {
        uint256 vTokenAmt = nftsCount * 1 ether;

        (ethRequired, , , ) = quoter.quoteExactOutputSingle(
            IQuoterV2.QuoteExactOutputSingleParams({
                tokenIn: WETH,
                tokenOut: address(vtoken),
                amount: vTokenAmt,
                fee: fee,
                sqrtPriceLimitX96: sqrtPriceLimitX96
            })
        );
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function getPoolExists(
        uint256 vaultId,
        uint24 fee
    ) external view override returns (address pool, bool exists) {
        address vToken_ = nftxVaultFactory.vault(vaultId);
        pool = IUniswapV3Factory(router.factory()).getPool(vToken_, WETH, fee);

        exists = pool != address(0);
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function getPoolExists(
        address vToken_,
        uint24 fee
    ) external view override returns (address pool, bool exists) {
        pool = IUniswapV3Factory(router.factory()).getPool(vToken_, WETH, fee);

        exists = pool != address(0);
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function getPool(
        address vToken_,
        uint24 fee
    ) external view override returns (address pool) {
        pool = IUniswapV3Factory(router.factory()).getPool(vToken_, WETH, fee);
        if (pool == address(0)) revert();
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function computePool(
        address vToken_,
        uint24 fee
    ) external view override returns (address) {
        return
            PoolAddress.computeAddress(
                router.factory(),
                PoolAddress.getPoolKey(vToken_, WETH, fee)
            );
    }

    /**
     * @inheritdoc INFTXRouter
     */
    function isVToken0(address vtoken) public view override returns (bool) {
        return vtoken < WETH;
    }

    // =============================================================
    //                      INTERNAL / PRIVATE
    // =============================================================

    function _addLiquidity(
        AddLiquidityParams calldata params,
        INFTXVaultV3 vToken
    ) internal returns (uint256 positionId) {
        (uint256 vTokensAmount, bool _isVToken0) = _pullAndMintVTokens(
            vToken,
            params.nftIds,
            params.nftAmounts,
            !vToken.is1155(),
            params.vTokensAmount
        );

        // creating struct first to avoid stack too deep. Variables not yet defined are set later
        INonfungiblePositionManager.MintParams
            memory mintParams = INonfungiblePositionManager.MintParams({
                token0: address(0),
                token1: address(0),
                fee: params.fee,
                tickLower: params.tickLower,
                tickUpper: params.tickUpper,
                amount0Desired: 0,
                amount1Desired: 0,
                amount0Min: 0,
                amount1Min: 0,
                recipient: msg.sender,
                deadline: params.deadline
            });

        // mint position with vtoken and ETH
        if (msg.value < params.wethMin) revert ETHValueLowerThanMin();
        (
            mintParams.token0,
            mintParams.token1,
            mintParams.amount0Desired,
            mintParams.amount1Desired,
            mintParams.amount0Min,
            mintParams.amount1Min
        ) = _isVToken0
            ? (
                address(vToken),
                WETH,
                vTokensAmount,
                msg.value,
                params.vTokenMin,
                params.wethMin
            )
            : (
                WETH,
                address(vToken),
                msg.value,
                vTokensAmount,
                params.wethMin,
                params.vTokenMin
            );

        address pool = positionManager.createAndInitializePoolIfNecessary(
            mintParams.token0,
            mintParams.token1,
            params.fee,
            params.sqrtPriceX96
        );

        // mint position with vtoken and ETH
        (positionId, , , ) = positionManager.mint{value: msg.value}(mintParams);

        _postAddLiq(
            vToken,
            params.nftIds,
            positionId,
            params.vaultId,
            params.forceTimelock
        );

        emit AddLiquidity(
            positionId,
            params.vaultId,
            params.vTokensAmount,
            params.nftIds,
            pool
        );
    }

    function _increaseLiquidity(
        IncreaseLiquidityParams calldata params,
        INFTXVaultV3 vToken
    ) internal {
        if (positionManager.ownerOf(params.positionId) != msg.sender)
            revert NotPositionOwner();

        (uint256 vTokensAmount, bool _isVToken0) = _pullAndMintVTokens(
            vToken,
            params.nftIds,
            params.nftAmounts,
            !vToken.is1155(),
            params.vTokensAmount
        );

        if (msg.value < params.wethMin) revert ETHValueLowerThanMin();
        (
            uint256 amount0Desired,
            uint256 amount1Desired,
            uint256 amount0Min,
            uint256 amount1Min
        ) = _isVToken0
                ? (vTokensAmount, msg.value, params.vTokenMin, params.wethMin)
                : (msg.value, vTokensAmount, params.wethMin, params.vTokenMin);

        positionManager.increaseLiquidity{value: msg.value}(
            INonfungiblePositionManager.IncreaseLiquidityParams({
                tokenId: params.positionId,
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired,
                amount0Min: amount0Min,
                amount1Min: amount1Min,
                deadline: params.deadline
            })
        );

        _postAddLiq(
            vToken,
            params.nftIds,
            params.positionId,
            params.vaultId,
            params.forceTimelock
        );

        emit IncreaseLiquidity(
            params.positionId,
            params.vaultId,
            params.vTokensAmount,
            params.nftIds
        );
    }

    function _pullAndMintVTokens(
        INFTXVaultV3 vToken,
        uint256[] calldata nftIds,
        uint256[] calldata nftAmounts,
        bool is721,
        uint256 currentVTokensAmount
    ) internal returns (uint256 vTokensAmount, bool _isVToken0) {
        vTokensAmount = currentVTokensAmount;

        if (nftIds.length > 0) {
            address assetAddress = vToken.assetAddress();

            if (is721) {
                // tranfer NFTs from user to the vault
                TransferLib.transferFromERC721(
                    assetAddress,
                    address(vToken),
                    nftIds
                );
            } else {
                IERC1155(assetAddress).safeBatchTransferFrom(
                    msg.sender,
                    address(this),
                    nftIds,
                    nftAmounts,
                    ""
                );

                IERC1155(assetAddress).setApprovalForAll(address(vToken), true);
            }

            // vault won't charge mintFees here as this contract is on exclude list
            vTokensAmount += vToken.mint(
                nftIds,
                nftAmounts,
                msg.sender,
                address(this)
            );
        }

        TransferLib.unSafeMaxApprove(
            address(vToken),
            address(positionManager),
            vTokensAmount
        );

        _isVToken0 = isVToken0(address(vToken));
    }

    function _postAddLiq(
        INFTXVaultV3 vToken,
        uint256[] calldata nftIds,
        uint256 positionId,
        uint256 vaultId,
        bool forceTimelock
    ) internal {
        uint256 vTokenBalance = vToken.balanceOf(address(this));
        if (nftIds.length > 0) {
            // vault fees not charged, so instead update timelock of the position NFT
            uint256 _lpTimelock = lpTimelock;
            positionManager.setLockedUntil(
                positionId,
                block.timestamp + _lpTimelock,
                _lpTimelock
            );

            if (vTokenBalance > 0) {
                if (vTokenBalance > vTokenDustThreshold) {
                    // stake vTokens left in the inventory
                    TransferLib.unSafeMaxApprove(
                        address(vToken),
                        address(inventoryStaking),
                        vTokenBalance
                    );

                    inventoryStaking.deposit(
                        vaultId,
                        vTokenBalance,
                        msg.sender,
                        "",
                        false,
                        true // forceTimelock as we minted the vTokens with NFTs
                    );
                } else {
                    vToken.transfer(msg.sender, vTokenBalance);
                }
            }
        } else {
            // if forcing timelock requested
            if (forceTimelock) {
                uint256 _lpTimelock = lpTimelock;
                positionManager.setLockedUntil(
                    positionId,
                    block.timestamp + _lpTimelock,
                    _lpTimelock
                );
            }

            // refund vTokens dust (if any left)
            if (vTokenBalance > 0) {
                vToken.transfer(msg.sender, vTokenBalance);
            }
        }

        // refund extra ETH
        positionManager.refundETH(msg.sender);
    }

    function _distributeVaultFees(
        uint256 vaultId,
        uint256 ethAmount,
        bool isWeth
    ) internal {
        if (ethAmount > 0) {
            INFTXFeeDistributorV3 feeDistributor = INFTXFeeDistributorV3(
                nftxVaultFactory.feeDistributor()
            );
            if (!isWeth) {
                IWETH9(WETH).deposit{value: ethAmount}();
            }
            IWETH9(WETH).transfer(address(feeDistributor), ethAmount);
            feeDistributor.distribute(vaultId);
        }
    }

    function _sum1155Ids(
        uint256[] calldata ids,
        uint256[] calldata amounts
    ) internal pure returns (uint256 totalAmount) {
        for (uint i; i < ids.length; ) {
            unchecked {
                // simultaneously verifies that lengths of `ids` and `amounts` match.
                totalAmount += amounts[i];
                ++i;
            }
        }
    }

    function _ethMintFees(
        INFTXVaultV3 vToken,
        uint256 nftCount
    ) internal view returns (uint256) {
        (uint256 mintFee, , ) = vToken.vaultFees();

        return vToken.vTokenToETH(mintFee * nftCount);
    }

    receive() external payable {}
}

src/NFTXVaultFactoryUpgradeableV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.15;

// inheriting
import {UpgradeableBeacon} from "@src/custom/proxy/UpgradeableBeacon.sol";
import {PausableUpgradeable} from "@src/custom/PausableUpgradeable.sol";

// libs
import {TickMath} from "@uniswap/v3-core/contracts/libraries/TickMath.sol";
import {FullMath} from "@uniswap/v3-core/contracts/libraries/FullMath.sol";
import {FixedPoint96} from "@uniswap/v3-core/contracts/libraries/FixedPoint96.sol";
import {ExponentialPremium} from "@src/lib/ExponentialPremium.sol";

// contracts
import {BeaconProxy} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {NFTXVaultUpgradeableV3} from "@src/NFTXVaultUpgradeableV3.sol";

// interfaces
import {INFTXVaultV3} from "@src/interfaces/INFTXVaultV3.sol";
import {IUniswapV3Pool} from "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import {IUniswapV3PoolDerivedState} from "@uniswap/v3-core/contracts/interfaces/pool/IUniswapV3PoolDerivedState.sol";

import {INFTXVaultFactoryV3} from "@src/interfaces/INFTXVaultFactoryV3.sol";

// Authors: @0xKiwi_, @alexgausman and @apoorvlathey

contract NFTXVaultFactoryUpgradeableV3 is
    INFTXVaultFactoryV3,
    PausableUpgradeable,
    UpgradeableBeacon
{
    // =============================================================
    //                            CONSTANTS
    // =============================================================
    uint256 constant MAX_DEPOSITOR_PREMIUM_SHARE = 1 ether;

    // =============================================================
    //                            VARIABLES
    // =============================================================

    address public override feeDistributor;
    address public override eligibilityManager;

    mapping(address => address[]) internal _vaultsForAsset;

    address[] internal _vaults;

    mapping(address => bool) public override excludedFromFees;

    mapping(uint256 => VaultFees) internal _vaultFees;

    uint64 public override factoryMintFee;
    uint64 public override factoryRedeemFee;
    uint64 public override factorySwapFee;

    uint32 public override twapInterval;
    // time during which a deposited tokenId incurs premium during withdrawal from the vault
    uint256 public override premiumDuration;
    // max premium value in vTokens when NFT just deposited
    uint256 public override premiumMax;
    // fraction in wei, what portion of the premium to send to the NFT depositor
    uint256 public override depositorPremiumShare;

    // =============================================================
    //                           INIT
    // =============================================================

    function __NFTXVaultFactory_init(
        address vaultImpl,
        uint32 twapInterval_,
        uint256 premiumDuration_,
        uint256 premiumMax_,
        uint256 depositorPremiumShare_
    ) public override initializer {
        __Pausable_init();
        // We use a beacon proxy so that every child contract follows the same implementation code.
        __UpgradeableBeacon__init(vaultImpl);
        setFactoryFees(0.1 ether, 0.1 ether, 0.1 ether);

        if (twapInterval_ == 0) revert ZeroTwapInterval();
        if (depositorPremiumShare_ > MAX_DEPOSITOR_PREMIUM_SHARE)
            revert DepositorPremiumShareExceedsLimit();

        twapInterval = twapInterval_;
        premiumDuration = premiumDuration_;
        premiumMax = premiumMax_;
        depositorPremiumShare = depositorPremiumShare_;
    }

    // =============================================================
    //                     PUBLIC / EXTERNAL WRITE
    // =============================================================

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function createVault(
        string memory name,
        string memory symbol,
        address assetAddress,
        bool is1155,
        bool allowAllItems
    ) external override returns (uint256 vaultId) {
        onlyOwnerIfPaused(0);

        if (feeDistributor == address(0)) revert FeeDistributorNotSet();
        if (implementation() == address(0)) revert VaultImplementationNotSet();

        address vaultAddr = _deployVault(
            name,
            symbol,
            assetAddress,
            is1155,
            allowAllItems
        );

        vaultId = _vaults.length;
        _vaultsForAsset[assetAddress].push(vaultAddr);
        _vaults.push(vaultAddr);

        emit NewVault(vaultId, vaultAddr, assetAddress, name, symbol);
    }

    // =============================================================
    //                     ONLY PRIVILEGED WRITE
    // =============================================================

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setFactoryFees(
        uint256 mintFee,
        uint256 redeemFee,
        uint256 swapFee
    ) public override onlyOwner {
        if (mintFee > 0.5 ether) revert FeeExceedsLimit();
        if (redeemFee > 0.5 ether) revert FeeExceedsLimit();
        if (swapFee > 0.5 ether) revert FeeExceedsLimit();

        factoryMintFee = uint64(mintFee);
        factoryRedeemFee = uint64(redeemFee);
        factorySwapFee = uint64(swapFee);

        emit UpdateFactoryFees(mintFee, redeemFee, swapFee);
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setVaultFees(
        uint256 vaultId,
        uint256 mintFee,
        uint256 redeemFee,
        uint256 swapFee
    ) external override {
        if (msg.sender != owner()) {
            address vaultAddr = _vaults[vaultId];
            if (msg.sender != vaultAddr) revert CallerIsNotVault();
        }
        if (mintFee > 0.5 ether) revert FeeExceedsLimit();
        if (redeemFee > 0.5 ether) revert FeeExceedsLimit();
        if (swapFee > 0.5 ether) revert FeeExceedsLimit();

        _vaultFees[vaultId] = VaultFees(
            true,
            uint64(mintFee),
            uint64(redeemFee),
            uint64(swapFee)
        );
        emit UpdateVaultFees(vaultId, mintFee, redeemFee, swapFee);
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function disableVaultFees(uint256 vaultId) external override {
        if (msg.sender != owner()) {
            address vaultAddr = _vaults[vaultId];
            if (msg.sender != vaultAddr) revert CallerIsNotVault();
        }
        delete _vaultFees[vaultId];
        emit DisableVaultFees(vaultId);
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setFeeDistributor(
        address feeDistributor_
    ) external override onlyOwner {
        if (feeDistributor_ == address(0)) revert ZeroAddress();

        emit NewFeeDistributor(feeDistributor, feeDistributor_);
        feeDistributor = feeDistributor_;
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setFeeExclusion(
        address excludedAddr,
        bool excluded
    ) external override onlyOwner {
        excludedFromFees[excludedAddr] = excluded;
        emit FeeExclusion(excludedAddr, excluded);
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setEligibilityManager(
        address eligibilityManager_
    ) external override onlyOwner {
        emit NewEligibilityManager(eligibilityManager, eligibilityManager_);
        eligibilityManager = eligibilityManager_;
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setTwapInterval(uint32 twapInterval_) external override onlyOwner {
        if (twapInterval_ == 0) revert ZeroTwapInterval();

        twapInterval = twapInterval_;

        emit NewTwapInterval(twapInterval_);
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setPremiumDuration(
        uint256 premiumDuration_
    ) external override onlyOwner {
        premiumDuration = premiumDuration_;

        emit NewPremiumDuration(premiumDuration_);
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setPremiumMax(uint256 premiumMax_) external override onlyOwner {
        premiumMax = premiumMax_;

        emit NewPremiumMax(premiumMax_);
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function setDepositorPremiumShare(
        uint256 depositorPremiumShare_
    ) external override onlyOwner {
        if (depositorPremiumShare_ > MAX_DEPOSITOR_PREMIUM_SHARE)
            revert DepositorPremiumShareExceedsLimit();

        depositorPremiumShare = depositorPremiumShare_;

        emit NewDepositorPremiumShare(depositorPremiumShare_);
    }

    // =============================================================
    //                     PUBLIC / EXTERNAL VIEW
    // =============================================================

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function vaultFees(
        uint256 vaultId
    )
        external
        view
        override
        returns (uint256 mintFee, uint256 redeemFee, uint256 swapFee)
    {
        VaultFees memory fees = _vaultFees[vaultId];
        if (fees.active) {
            return (
                uint256(fees.mintFee),
                uint256(fees.redeemFee),
                uint256(fees.swapFee)
            );
        }

        return (
            uint256(factoryMintFee),
            uint256(factoryRedeemFee),
            uint256(factorySwapFee)
        );
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function getVTokenPremium721(
        uint256 vaultId,
        uint256 tokenId
    ) external view override returns (uint256 premium, address depositor) {
        INFTXVaultV3 _vault = INFTXVaultV3(_vaults[vaultId]);

        if (_vault.holdingsContains(tokenId)) {
            uint48 timestamp;
            (timestamp, depositor) = _vault.tokenDepositInfo(tokenId);

            premium = _getVTokenPremium(timestamp, premiumMax, premiumDuration);
        }
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function getVTokenPremium1155(
        uint256 vaultId,
        uint256 tokenId,
        uint256 amount
    )
        external
        view
        override
        returns (
            uint256 netPremium,
            uint256[] memory premiums,
            address[] memory depositors
        )
    {
        INFTXVaultV3 _vault = INFTXVaultV3(_vaults[vaultId]);

        if (_vault.holdingsContains(tokenId)) {
            if (amount == 0) revert ZeroAmountRequested();

            // max possible array lengths
            premiums = new uint256[](amount);
            depositors = new address[](amount);

            uint256 _pointerIndex1155 = _vault.pointerIndex1155(tokenId);

            uint256 i = 0;
            // cache
            uint256 _premiumMax = premiumMax;
            uint256 _premiumDuration = premiumDuration;
            uint256 _tokenPositionLength = _vault.depositInfo1155Length(
                tokenId
            );
            while (true) {
                if (_tokenPositionLength <= _pointerIndex1155 + i)
                    revert NFTInventoryExceeded();

                (uint256 qty, address depositor, uint48 timestamp) = _vault
                    .depositInfo1155(tokenId, _pointerIndex1155 + i);

                if (qty > amount) {
                    uint256 vTokenPremium = _getVTokenPremium(
                        timestamp,
                        _premiumMax,
                        _premiumDuration
                    ) * amount;
                    netPremium += vTokenPremium;

                    premiums[i] = vTokenPremium;
                    depositors[i] = depositor;

                    // end loop
                    break;
                } else {
                    amount -= qty;

                    uint256 vTokenPremium = _getVTokenPremium(
                        timestamp,
                        _premiumMax,
                        _premiumDuration
                    ) * qty;
                    netPremium += vTokenPremium;

                    premiums[i] = vTokenPremium;
                    depositors[i] = depositor;

                    unchecked {
                        ++i;
                    }
                }
            }

            uint256 finalArrayLength = i + 1;

            if (finalArrayLength < premiums.length) {
                // change array length
                assembly {
                    mstore(premiums, finalArrayLength)
                    mstore(depositors, finalArrayLength)
                }
            }
        }
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function isLocked(uint256 lockId) external view override returns (bool) {
        return isPaused[lockId];
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function vaultsForAsset(
        address assetAddress
    ) external view override returns (address[] memory) {
        return _vaultsForAsset[assetAddress];
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function allVaults() external view override returns (address[] memory) {
        return _vaults;
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function numVaults() external view override returns (uint256) {
        return _vaults.length;
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function vault(uint256 vaultId) external view override returns (address) {
        return _vaults[vaultId];
    }

    /**
     * @inheritdoc INFTXVaultFactoryV3
     */
    function getTwapX96(
        address pool
    ) external view override returns (uint256 priceX96) {
        // secondsAgos[0] (from [before]) -> secondsAgos[1] (to [now])
        uint32[] memory secondsAgos = new uint32[](2);
        secondsAgos[0] = twapInterval;
        secondsAgos[1] = 0;

        (bool success, bytes memory data) = pool.staticcall(
            abi.encodeWithSelector(
                IUniswapV3PoolDerivedState.observe.selector,
                secondsAgos
            )
        );

        // observe might fail for newly created pools that don't have sufficient observations yet
        if (!success) {
            // observations = [0, 1, 2, ..., index, (index + 1), ..., (cardinality - 1)]
            // Case 1: if entire array initialized once, then oldest observation at (index + 1) % cardinality
            // Case 2: array only initialized till index, then oldest obseravtion at index 0

            // Check Case 1
            (, , uint16 index, uint16 cardinality, , , ) = IUniswapV3Pool(pool)
                .slot0();

            (
                uint32 oldestAvailableTimestamp,
                ,
                ,
                bool initialized
            ) = IUniswapV3Pool(pool).observations((index + 1) % cardinality);

            // Case 2
            if (!initialized)
                (oldestAvailableTimestamp, , , ) = IUniswapV3Pool(pool)
                    .observations(0);

            // get corresponding observation
            secondsAgos[0] = uint32(block.timestamp - oldestAvailableTimestamp);
            (success, data) = pool.staticcall(
                abi.encodeWithSelector(
                    IUniswapV3PoolDerivedState.observe.selector,
                    secondsAgos
                )
            );
            // might revert if oldestAvailableTimestamp == block.timestamp, so we return price as 0
            if (!success || secondsAgos[0] == 0) {
                return 0;
            }
        }

        int56[] memory tickCumulatives = abi.decode(data, (int56[])); // don't bother decoding the liquidityCumulatives array

        uint160 sqrtPriceX96 = TickMath.getSqrtRatioAtTick(
            int24(
                (tickCumulatives[1] - tickCumulatives[0]) /
                    int56(int32(secondsAgos[0]))
            )
        );
        priceX96 = FullMath.mulDiv(
            sqrtPriceX96,
            sqrtPriceX96,
            FixedPoint96.Q96
        );
    }

    // =============================================================
    //                        INTERNAL HELPERS
    // =============================================================

    function _deployVault(
        string memory name,
        string memory symbol,
        address assetAddress,
        bool is1155,
        bool allowAllItems
    ) internal returns (address) {
        address newBeaconProxy = address(new BeaconProxy(address(this), ""));
        NFTXVaultUpgradeableV3(newBeaconProxy).__NFTXVault_init(
            name,
            symbol,
            assetAddress,
            is1155,
            allowAllItems
        );
        // Manager for configuration.
        NFTXVaultUpgradeableV3(newBeaconProxy).setManager(msg.sender);
        // Owner for administrative functions.
        NFTXVaultUpgradeableV3(newBeaconProxy).transferOwnership(owner());
        return newBeaconProxy;
    }

    function _getVTokenPremium(
        uint48 timestamp,
        uint256 _premiumMax,
        uint256 _premiumDuration
    ) internal view returns (uint256) {
        return
            ExponentialPremium.getPremium(
                timestamp,
                _premiumMax,
                _premiumDuration
            );
    }
}

